#include <stdio.h>
#include <string.h>

int main(void)
{
    FILE *fd;
    int ret;
    char *buf= "";
    char *buf2 = "";

    fd = fopen("mountdir/test.txt", "w");
    if (fd < 0) {
        perror("open");
        return -1;
    }

    ret = fwrite(buf, 4096, sizeof(char), fd);
    if (ret < 0) {
        perror("write");
        return -1;
    }

    ret = fwrite(buf2, 4096, sizeof(char), fd);
    if (ret < 0) {
        perror("write");
        return -1;
    }

    ret = fwrite(buf2, 4096, sizeof(char), fd);
    if (ret < 0) {
        perror("write");
        return -1;
    }

    fclose(fd);

    return 0;
}
